function scrollTo () {
  $('#theme_nav li a').click(function(e) {
      e.preventDefault();
      $('.mainMenu a').removeClass('active');
      $(this).addClass('active');

     var distanceTopToSection = $( '#' +  $(this).data('target')).offset().top;

    		 $( 'body, html' ).animate({scrollTop:distanceTopToSection }, 'slow');
  });
}

